const express = require("express");
const app = express();
app.listen(3000);

// 3. Crea un array de 5 nombres. Define dos rutas: una será del tipo “/persona” y la otra será /persona/:parámetro’. Al entrar a la primera ruta nos devolverá la lista de personas y al entrar a la segunda nos devolverá la persona solicitada.

let array = ["Erlantz", "Peio", "Carla", "Julen", "Dylan"];

app.get("/persona", function(req, res){
    res.send(array)
})


app.get("/persona/:parametro", function(req, res){
    let parametro = req.params.parametro;
    let exist = false;
    array.forEach(element=>{
        if(element.toLowerCase() === parametro.toLowerCase()){
            exist = true;
        }
    })
    if(exist){
        res.send(parametro);
    } else {
        res.send("El nombre no esta en la lista");
    }
})